  let maxRounds = 3;
  let isLoading = false;
  let showHistory = false;
  let actionHistory = [];
  let activeQuests = [];
  let userHeroes = [];
  let showQuestLog = false;
  let showTavern = false;
  let eventLog = [];
  let showConnectionPrompt = false;
